/** Get all states from India */
export function getAllStates(): string[];

/**
 * Get districts for a given state
 * @param stateName - The name of the state
 * @returns Array of district names or null if state not found
 */
export function getDistrictsByState(stateName: string): string[] | null;

/**
 * Get all states with their districts
 * @returns Array of objects containing state name and districts
 */
export function getAllStatesWithDistricts(): Array<{
  name: string;
  districts: string[];
}>;

/**
 * Check if a state exists
 * @param stateName - The name of the state
 * @returns True if state exists, false otherwise
 */
export function stateExists(stateName: string): boolean;

